    <script>
        $(window).scroll(function() {
            var scroll = $(window).scrollTop();
            if (scroll > 10) {
                $(".navbar-header").css("background", "var(--secondary-color)");
                // $("li.nav-item span").css("color", "white");
                // $("li.nav-item.active span").css("border-bottom", "2px solid white");
                $("li.nav-item span").addClass("scrolled");
                $("li.nav-item.active span").addClass("scrolled");
            } else {
                $(".navbar-header").css("background", "transparent");
                // $("li.nav-item span").css("color", "var(--secondary-color)");
                // $("li.nav-item.active span").css("border-bottom", "2px solid var(--secondary-color)");
                $("li.nav-item span").removeClass("scrolled");
                $("li.nav-item.active span").removeClass("scrolled");
            }
        })
		jQuery(
		  function($) {
			 var footer = $('footer'),
				btnTop = $('.tanya-bodrex');
			  
			$(window).scroll(function() {

                if ((btnTop.offset().top + btnTop.height()) >= (footer.offset().top - 10)) {
                    btnTop.addClass('tanya-bodrex--bottom');
                };

                if (($(document).scrollTop() + window.innerHeight) < (footer.offset().top)) {
                    btnTop.removeClass('tanya-bodrex--bottom');
                };
            });
		  }
		);
    </script>

