    <script>
        var $base_url = 'https://www.bodrex.com/';
		
				
				
        $('.load-more').click(function() {
            $(this).hide();
            var id = $('.item:last').attr('id');
            $.ajax({
                url: $base_url + "home/load_more/" + $("#kategori").val() + '/' + id,
            }).done(function(html) {
                $("#article-list").append(html);
                $('.load-more').show();
            });


        });

        $("#load-pertanyaan").click(function() {
            $(this).hide();
            var id = $('.item-pertanyaan:last').attr('id');
            $.ajax({
                url: $base_url + "tanyabodrex/load_more/" + id,
            }).done(function(html) {

                $("#q-box").append(html);
                $("#load-pertanyaan").show();
            });
        });

        $(".submit").click(function() {
            //$("#question-form input").css('border', 'none')
            var params = $("#question-form").serialize();
            $.ajax({
                type: 'POST',
                url: $base_url + "tanyabodrex/submit_question/",
                data: params,
                dataType: 'json',
                async: false,
                success: function(datax) {
                    if (datax.status == 1) {
                        $("#question-form input").val('');
                        $("#question-form textarea").val('');
                        $(".alert-success").show();
                    } else if (datax.status == 2) {
                        $(".alert-warning").show();
                    } else {
                        $(".alert-danger").show();
                    }
                },
                error: function(x) {
                    console.log("Error: " + "\r\n\r\n" + x.responseText);
                }
            });


            return false;
        });

